    const classes = classNames(
      {
        'it-opener d-lg-none': HeaderType === SLIM,
        'custom-navbar-toggler': HeaderType === NAVBAR
      },
      className
    );
    return (
      <NavbarToggler
        className={classes}
        {...attributes}
        tag={tag || defaultTag}
        type={type || defaultType}
        href='#'
      />
    );
  }
