import React from 'react';

import {
  Breadcrumb,
  BreadcrumbItem,
  Container,
  Button,
} from 'design-react-kit';
import cx from 'classnames';

import 'bootstrap-italia/dist/css/bootstrap-italia.min.css';
import './App.scss';

import 'typeface-titillium-web';
import 'typeface-roboto-mono';
import 'typeface-lora';

const App = () => {
  return (
    <div className="App">
      <div className="ricerca-moduli">
        <Container className="page-header">
          <div className="breadcrumb-container">
            <Breadcrumb>
              <BreadcrumbItem>
                <a href="#">Home</a>
                <span className="separator">/</span>
              </BreadcrumbItem>
              <BreadcrumbItem>
                <a href="#">...</a>
                <span className="separator">/</span>
              </BreadcrumbItem>
              <BreadcrumbItem active>
                Motore di ricerca per moduli e documenti
              </BreadcrumbItem>
            </Breadcrumb>
          </div>

          <div className="text-container">
            <h2>Motore di ricerca per moduli e documenti</h2>
            <p>
              Per restringere i risultati della ricerca selezionare le categorie
              di interesse nei men√π a tendina.
            </p>
          </div>
        </Container>
        <Container className="search-group">
          <Container>
            <div
              className={cx('rounded bg-', 'primary', {
                // 'public-ui': inEditMode,
              })}
            >
              <div className="d-flex justify-content-center">
                <div className="d-flex search-container align-items-center justify-content-center flex-wrap">
                  {/* {filterOne && ( */}
                  {
                    <>
                      {React.createElement(filterOne.widget.component, {
                        ...filterOne.widget?.props,
                        id: 'filterOne',
                        onChange: (filter, value) => {
                          dispatchFilter({
                            filter: filter,
                            value: value,
                          });
                        },
                      })}
                    </>
                  }
                  {/* {filterTwo && */}
                  {React.createElement(filterTwo.widget?.component, {
                    ...filterTwo.widget?.props,
                    id: 'filterTwo',
                    onChange: (filter, value) =>
                      dispatchFilter({
                        filter: filter,
                        value: value,
                      }),
                  })}
                  {/* {filterThree && */}
                  {React.createElement(filterThree.widget?.component, {
                    ...filterThree.widget?.props,
                    id: 'filterThree',
                    onChange: (filter, value) =>
                      dispatchFilter({
                        filter: filter,
                        value: value,
                      }),
                  })}

                  <Button
                    color={'tertiary'}
                    icon={false}
                    tag="button"
                    onClick={() => doRequest(1)}
                    className="my-2 my-lg-1"
                  >
                    {/* {intl.formatMessage(messages.find)} */}
                  </Button>
                </div>
              </div>
            </div>
          </Container>
        </Container>
        <Container className="result-table"></Container>
      </div>
    </div>
  );
};

export default App;
