import React from 'react';

import SearchBox from './components/SearchBox/SearchBox.component';
import SelectBox from './components/SelectBox/SelectBox.component';

import {
  Breadcrumb,
  BreadcrumbItem,
  Container,
  Button,
} from 'design-react-kit';
import cx from 'classnames';

import 'bootstrap-italia/dist/css/bootstrap-italia.min.css';
import './App.scss';

import 'typeface-titillium-web';
import 'typeface-roboto-mono';
import 'typeface-lora';

const App = () => {
  return (
    <div className="App">
      <div className="ricerca-moduli">
        <Container className="page-header">
          <div className="breadcrumb-container">
            <Breadcrumb>
              <BreadcrumbItem>
                <a href="#">Home</a>
                <span className="separator">/</span>
              </BreadcrumbItem>
              <BreadcrumbItem>
                <a href="#">...</a>
                <span className="separator">/</span>
              </BreadcrumbItem>
              <BreadcrumbItem active>
                Motore di ricerca per moduli e documenti
              </BreadcrumbItem>
            </Breadcrumb>
          </div>

          <div className="text-container">
            <h2>Motore di ricerca per moduli e documenti</h2>
            <p>
              Per restringere i risultati della ricerca selezionare le categorie
              di interesse nei men√π a tendina.
            </p>
          </div>

          <div
            className={cx(
              'rounded bg-',
              'primary',
              'search-group-container',
              'search-group'
              // {
              //   'public-ui': inEditMode,
              // }
            )}
          >
            <SearchBox />
            <div className="filters-line">
              <SelectBox />
              <Button
                color={'tertiary'}
                icon={false}
                tag="button"
                // onClick={() => doRequest(1)}
                className="my-2 my-lg-1"
              >
                hello{/* {intl.formatMessage(messages.find)} */}
              </Button>
            </div>
          </div>
        </Container>
        <Container className="result-table"></Container>
      </div>
    </div>
  );
};

export default App;
