import React from 'react';

import { Button } from 'design-react-kit';
import { Form } from 'react-bootstrap/Form';
import cx from 'classnames';

import './SearchBox.styles.scss';

const SearchBox = () => {
  return (
    <div
      className={cx(
        'rounded bg-',
        'primary',
        'search-group-container'
        // {
        //   'public-ui': inEditMode,
        // }
      )}
    >
      <div className="d-flex justify-content-center search-box-container">
        <input
          className="search-box rounded"
          type="search"
          placeholder="Digita la parola chiave"
          // onChange={onChangeSearch}
        />
      </div>
      <div className="select-menu-container">
        <div className="bootstrap-select-wrapper">
          {/* <label htmlFor="selectExampleClassic">Etichetta di esempio</label> */}
          <Form.Select aria-label="Default select example">
            <option>Open this selct menu</option>
            <option value="1">One</option>
            <option value="2">Two</option>
            <option value="3">Three</option>
          </Form.Select>
        </div>

        <Button
          color={'tertiary'}
          icon={false}
          tag="button"
          // onClick={() => doRequest(1)}
          className="my-2 my-lg-1"
        >
          hello{/* {intl.formatMessage(messages.find)} */}
        </Button>
      </div>
    </div>
  );
};

export default SearchBox;
