import { Rect, Scroll, Dimensions, OnUpdateType } from './types';

export const shallowEqualScroll = (a: Scroll, b: Scroll) => {
  if (a === b) {
    return true;
  }
  return (
    a.x === b.x &&
    a.y === b.y &&
    a.xTurn === b.xTurn &&
    a.yTurn === b.yTurn &&
    a.xDTurn === b.xDTurn &&
    a.yDTurn === b.yDTurn &&
    a.isScrollingUp === b.isScrollingUp &&
    a.isScrollingDown === b.isScrollingDown &&
    a.isScrollingLeft === b.isScrollingLeft &&
    a.isScrollingRight === b.isScrollingRight
  );
};

export const shallowEqualRect = (a: Rect, b: Rect) => {
  if (a === b) {
    return true;
  }

  return (
    a.top === b.top &&
    a.right === b.right &&
    a.bottom === b.bottom &&
    a.left === b.left &&
    a.height === b.height &&
    a.width === b.width
  );
};

export const shallowEqualDimensions = (a: Dimensions, b: Dimensions) => {
  if (a === b) {
    return true;
  }

  return (
    a.width === b.width &&
    a.height === b.height &&
    a.clientWidth === b.clientWidth &&
    a.clientHeight === b.clientHeight &&
    a.outerWidth === b.outerWidth &&
    a.outerHeight === b.outerHeight &&
    a.documentWidth === b.documentWidth &&
    a.documentHeight === b.documentHeight
  );
};

// implementation based on https://github.com/WICG/EventListenerOptions/blob/gh-pages/explainer.md
export const browserSupportsPassiveEvents = (() => {
  if (typeof window === 'undefined') {
    return false;
  }
  let supportsPassive = false;
  try {
    const opts = Object.defineProperty({}, 'passive', {
      get: () => {
        supportsPassive = true;
      },
    });
    window.addEventListener('testPassive', null as any, opts);
    window.removeEventListener('testPassive', null as any, opts);
  } catch (e) {
    return false;
  }
  return supportsPassive;
})();

export const simpleDebounce = <F extends (...args: any[]) => any>(
  fn: F,
  delay: number,
): F => {
  let timeout: NodeJS.Timer;
  return ((...args: any[]) => {
    clearTimeout(timeout);
    timeout = setTimeout(fn, delay, ...args);
  }) as F;
};

export const debounceOnUpdate = (
  fn: OnUpdateType,
  delay: number,
): OnUpdateType => {
  let timeout: NodeJS.Timer;
  let scrollDidUpdate = false;
  let dimensionsDidUpdate = false;

  return (viewport, options) => {
    clearTimeout(timeout);
    scrollDidUpdate = scrollDidUpdate || options.scrollDidUpdate;
    dimensionsDidUpdate = dimensionsDidUpdate || options.dimensionsDidUpdate;
    timeout = setTimeout(() => {
      fn(viewport, {
        scrollDidUpdate,
        dimensionsDidUpdate,
      });
      scrollDidUpdate = false;
      dimensionsDidUpdate = false;
    }, delay);
  };
};

export const warnNoContextAvailable = (location: string) => {
  if (process.env.NODE_ENV === 'production') {
    return;
  }
  const fromHook = location.startsWith('use');
  if (fromHook) {
    console.warn(
      `react-viewport-utils: ${location} hook is not able to connect to a <ViewportProvider>. Therefore it cannot detect updates from the viewport and will not work as expected. To resolve this issue please add a <ViewportProvider> as a parent of the component using the hook, e.g. directly in the ReactDOM.render call:

import * as ReactDOM from 'react-dom';
import { ViewportProvider, ${location} } from 'react-viewport-utils';

const MyComponent = () => {
  ${location}()
  ...
}

ReactDOM.render(
  <ViewportProvider>
    <main role="main">
      <MyComponent />
    </main>
  </ViewportProvider>,
  document.getElementById('root')
);`,
    );
    return;
  }
  console.warn(
    `react-viewport-utils: ${location} component is not able to connect to a <ViewportProvider>. Therefore it cannot detect updates from the viewport and will not work as expected. To resolve this issue please add a <ViewportProvider> as a parent of the <ObserveViewport> component, e.g. directly in the ReactDOM.render call:

import * as ReactDOM from 'react-dom';
import { ViewportProvider, ObserveViewport } from 'react-viewport-utils';
ReactDOM.render(
  <ViewportProvider>
    <main role="main">
      <ObserveViewport>
        {({ scroll, dimensions }) => ...}
      </ObserveViewport>
    </main>
  </ViewportProvider>,
  document.getElementById('root')
);`,
  );
};

export const warnNoResizeObserver = () => {
  if (process.env.NODE_ENV === 'production') {
    return;
  }
  console.warn(
    'react-viewport-utils: This browser does not support the ResizeObserver API, therefore not all possible resize events will be detected. In most of the cases this is not an issue and can be ignored. If its relevant to your application please consider adding a polyfill, e.g. https://www.npmjs.com/package/resize-observer-polyfill .',
  );
};

type RequestAnimationFrameType = (callback: FrameRequestCallback) => number;

export const requestAnimationFrame = ((): RequestAnimationFrameType => {
  if (typeof window !== 'undefined') {
    const nativeRAF =
      window.requestAnimationFrame ||
      window.webkitRequestAnimationFrame ||
      (<any>window).mozRequestAnimationFrame;
    if (nativeRAF) {
      return nativeRAF.bind(window);
    }
  }
  return function requestAnimationFrameFallback(
    callback: FrameRequestCallback,
  ): number {
    return (setTimeout(callback, 1000 / 60) as unknown) as number;
  };
})();

export const cancelAnimationFrame = ((): ((handle: number) => void) => {
  if (typeof window !== 'undefined') {
    const nativeCAF =
      window.cancelAnimationFrame ||
      window.webkitCancelAnimationFrame ||
      (<any>window).webkitCancelRequestAnimationFrame;
    if (nativeCAF) {
      return nativeCAF.bind(window);
    }
  }
  return clearTimeout;
})();

export const now =
  typeof performance !== 'undefined' && performance.now
    ? performance.now.bind(performance)
    : Date.now.bind(Date);
export const createPerformanceMarker = () => {
  const start = now();
  return () => now() - start;
};
import * as React from 'react';
import memoize from 'memoize-one';

import {
  shallowEqualScroll,
  shallowEqualDimensions,
  browserSupportsPassiveEvents,
  simpleDebounce,
  debounceOnUpdate,
  warnNoResizeObserver,
  requestAnimationFrame,
  cancelAnimationFrame,
} from './utils';

import { Dimensions, Scroll, Viewport, OnUpdateType } from './types';

export const getClientDimensions = (): Dimensions => {
  if (typeof document === 'undefined' || !document.documentElement) {
    return createEmptyDimensionState();
  }
  const { innerWidth, innerHeight, outerWidth, outerHeight } = window;
  const {
    clientWidth,
    clientHeight,
    scrollHeight,
    scrollWidth,
    offsetHeight,
    offsetWidth,
  } = document.documentElement;
  return {
    width: innerWidth,
    height: innerHeight,
    clientWidth,
    clientHeight,
    outerWidth,
    outerHeight,
    documentWidth: Math.max(scrollWidth, offsetWidth, clientWidth),
    documentHeight: Math.max(scrollHeight, offsetHeight, clientHeight),
  };
};

const getNodeScroll = (elem = window) => {
  let { scrollX, scrollY } = elem;
  if (scrollX === undefined) {
    scrollX = elem.pageXOffset;
  }
  if (scrollY === undefined) {
    scrollY = elem.pageYOffset;
  }

  return {
    x: scrollX,
    y: scrollY,
  };
};

export const getClientScroll = (
  prevScrollState: Scroll = createEmptyScrollState(),
) => {
  if (typeof window === 'undefined') {
    return createEmptyScrollState();
  }
  const { x, y } = getNodeScroll();
  const nextScrollState = { ...prevScrollState };
  const {
    isScrollingLeft: prevIsScrollingLeft,
    isScrollingUp: prevIsScrollingUp,
    xTurn: prevXTurn,
    yTurn: prevYTurn,
  } = prevScrollState;

  nextScrollState.isScrollingLeft = isScrollingLeft(x, nextScrollState);
  nextScrollState.isScrollingRight = isScrollingRight(x, nextScrollState);

  nextScrollState.isScrollingUp = isScrollingUp(y, nextScrollState);
  nextScrollState.isScrollingDown = isScrollingDown(y, nextScrollState);

  nextScrollState.xTurn =
    nextScrollState.isScrollingLeft === prevIsScrollingLeft ? prevXTurn : x;
  nextScrollState.yTurn =
    nextScrollState.isScrollingUp === prevIsScrollingUp ? prevYTurn : y;

  nextScrollState.xDTurn = x - nextScrollState.xTurn;
  nextScrollState.yDTurn = y - nextScrollState.yTurn;

  nextScrollState.x = x;
  nextScrollState.y = y;

  return nextScrollState;
};

const isScrollingLeft = (x: number, prev: Scroll) => {
  switch (true) {
    case x < prev.x:
      return true;
    case x > prev.x:
      return false;
    case x === prev.x:
      return prev.isScrollingLeft;
    default:
      throw new Error('Could not calculate isScrollingLeft');
  }
};

const isScrollingRight = (x: number, prev: Scroll) => {
  switch (true) {
    case x > prev.x:
      return true;
    case x < prev.x:
      return false;
    case x === prev.x:
      return prev.isScrollingRight;
    default:
      throw new Error('Could not calculate isScrollingRight');
  }
};

const isScrollingUp = (y: number, prev: Scroll) => {
  switch (true) {
    case y < prev.y:
      return true;
    case y > prev.y:
      return false;
    case y === prev.y:
      return prev.isScrollingUp;
    default:
      throw new Error('Could not calculate isScrollingUp');
  }
};

const isScrollingDown = (y: number, prev: Scroll) => {
  switch (true) {
    case y > prev.y:
      return true;
    case y < prev.y:
      return false;
    case y === prev.y:
      return prev.isScrollingDown;
    default:
      throw new Error('Could not calculate isScrollingDown');
  }
};

export const createEmptyScrollState = () => ({
  x: 0,
  y: 0,
  isScrollingUp: false,
  isScrollingDown: false,
  isScrollingLeft: false,
  isScrollingRight: false,
  xTurn: 0,
  yTurn: 0,
  xDTurn: 0,
  yDTurn: 0,
});

export const createEmptyDimensionState = (): Dimensions => ({
  width: 0,
  height: 0,
  clientWidth: 0,
  clientHeight: 0,
  outerWidth: 0,
  outerHeight: 0,
  documentWidth: 0,
  documentHeight: 0,
});

interface IProps {
  onUpdate: OnUpdateType;
  onIdledUpdate?: OnUpdateType;
}

export default class ViewportCollector extends React.PureComponent<IProps> {
  private scrollState: Scroll;
  private dimensionsState: Dimensions;
  private lastSyncedScrollState: Scroll;
  private lastSyncedDimensionsState: Dimensions;
  private tickId?: number;
  private scrollMightHaveUpdated: boolean;
  private resizeMightHaveUpdated: boolean;
  private resizeObserver: ResizeObserver | null;
  public syncedStateOnce: boolean;

  constructor(props: IProps) {
    super(props);
    this.state = {
      parentProviderExists: false,
    };
    this.scrollMightHaveUpdated = false;
    this.resizeMightHaveUpdated = false;
    this.scrollState = createEmptyScrollState();
    this.dimensionsState = createEmptyDimensionState();
    this.lastSyncedDimensionsState = { ...this.dimensionsState };
    this.lastSyncedScrollState = { ...this.scrollState };
    this.resizeObserver = null;
    this.syncedStateOnce = false;
  }

  componentDidMount() {
    const options = browserSupportsPassiveEvents ? { passive: true } : false;
    window.addEventListener('scroll', this.handleScroll, options);
