        }
        return null;
      },
    });

    if (this.props.children) {
      this.syncState(getCurrentViewport());
    }

    return null;
  };

  render() {
    const { children } = this.props;
    return (
      <React.Fragment>
        <ViewportContext.Consumer>
          {this.registerViewportListeners}
        </ViewportContext.Consumer>
        {children ? children(this.state) : null}
      </React.Fragment>
